var fb_access_token = 'CAAGydn2NB1UBAOTd9Kcn76a2YVWX06PHQiv8tMDAul4kZCm4FgLcDeTQVzZCRYPZAmrdMrRtv1Mgnmij8ZBUNtRGtnyZCPvB4xy8UJlbeEPjUpf81rJnmJ2eFpTojQQYNZBNYK9rQUFnrvSOz14dFVtWO3vGrLszEZD&'
var fb_friends_url = '/me/friends';
var fb_newsfeed_url = '/me/home';


util.logger(util.INFO, 'New facebook news stream processor for user id('+')');

getFbData(fb_access_token, fb_newsfeed_url, function(data){
	
	var obj = JSON.parse(data);

	if(obj.hasOwnProperty('error')) throw obj.error.message;
	
	var posts = [];
	
	for(i in obj.data) {
        var item = obj.data[i];
        
        var type = item.type;
		var post = {};
		var urls = '';
		
		post.type = type;
		post.source = 'facebook'
		
		post.id = item.id;
		post.screen_name = '';

		post.from = {};
		post.from.id = item.from.id;
		post.from.name = item.from.name;
		post.time = item.created_time;

		post.text = item.message;
		post.hastags = new Array();
		post.urls = new Array();
		post.users = new Array();



		if(item.message != undefined) {
            urls = item.message.match(geturl);

            if(item.type == 'status' ) {
                util.debug(item.message,1);
                
                util.debug(urls,2);
            }



        }

        
        
        
		for(i in urls) {
			var url = {};
			
			url.short_url = urls[i];
			post.urls.push(url);
		}
				
		if(type == 'link') {
			var url = {};
			url.short_url = item.link;
			post.urls.push(url);
		}
		
        
		if(post.urls.length>0) posts.push(post);
    }

    async.eachSeries(posts, function(post, callback) {
        async.eachSeries(post.urls, function(url, callback) {
                
            netlib.expandUrl(url.short_url, function(err, data) {
                    if(err) {
                        util.logger(util.ERROR, err);
                    } 
                    else {
                        url.long_url = data;
                        callback();
                    }
            });
            
        }, function(err) {
            callback();
        });
    
    }, function(err) {
        
        for(i in posts) {
            var post = posts[i];

            util.logger(util.INFO, 'insert FB:'+post.type+' id(' + post.id + ') to db as id(' + 0 + ') for user id(' + post.from.id + ')');

        }

    });
    
});


